mapper.select:
select a.HTMLMODLE_ID, a.TITLE, a.SUBHEAD, a.CONTENT, a.IMG_URL, a.HTML_TYPE, a.SORT, a.ARTICLE_ID, a.HTML_URL, a.MEMO, a.CREATE_TIME, a.HTMLMODLE_DETAIL_ID from TB_HTMLMODLE_DETAIL a,TB_HTMLMODLE b where a.HTMLMODLE_ID=b.HTMLMODLE_ID
AND a.HTMLMODLE_ID = ${pd.HTMLMODLE_ID}
order by a.SORT asc

select a.HTMLMODLE_ID, a.TITLE, a.SUBHEAD, a.CONTENT, a.IMG_URL, a.HTML_TYPE, a.SORT, a.ARTICLE_ID, a.HTML_URL, a.MEMO, a.CREATE_TIME, a.HTMLMODLE_DETAIL_ID from TB_HTMLMODLE_DETAIL a,TB_HTMLMODLE b where a.HTMLMODLE_ID=b.HTMLMODLE_ID
AND a.HTMLMODLE_ID = ${HTMLMODLE_ID}
order by a.SORT asc
